import React from 'react';
import axios from 'axios';
import Dropdown from 'react-dropdown';

class Winners extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        winnersData: [],
        dropdownOptions: [],
        teamsBeaten: null
    };
  }

  componentDidMount() {
    this.getWinners();
  }

  getWinners() {
    axios.get(`https://raw.githubusercontent.com/ozmatron/work/master/footy-winners.json`)
    .then((response) => {
      this.setState({winnersData: response.data}) 
      this.setState({dropdownOptions: Object.keys(response.data)}) 
    })
  }

  onSelect = (event) =>  {
      let team = event.value


      Object.keys(this.state.winnersData).map(key => 
        key === team ?
        console.log(this.state.winnersData[key])
        : null
    )
  }

  render() {
    //   console.log(this.state.winnersData)
    //   Object.keys(this.state.winnersData).map(key => 
    //     console.log(this.state.winnersData[key])
    // )
    let dropdown = <Dropdown options={this.state.dropdownOptions} onChange={this.onSelect} value="Select a team" placeholder="Select a team" />

    console.log(this.state);
    return (
        <React.Fragment>
            <h1>"Champions League Challenge"</h1>
            {dropdown}
        </React.Fragment>
    );
  }
}

export default Winners;
