            userId: null,
            getImages: false,
            image: [],
        };
      }

      typingTimer = null;

      componentDidMount() {
        if (!this.state.userId) {
          this.getUserId();
        }
      }

      getUserId() {
        axios.get(`http://localhost:8080/api/user_data`)
        .then((response) => {
          this.setState({userId: response.data.id})
        })
      }

      getImages() {
        axios.get(`http://localhost:8080/api/images/` + this.state.userId)
