import React from 'react';
import axios from 'axios';

class TasksEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        userId: null,
        getTasks: false,
        tasks: [],
        newTask: ''
    };
  }

  typingTimer = null;

  componentDidMount() {
    if (!this.state.userId) {
      this.getUserId();
    }
  }

  getUserId() {
    axios.get(`http://localhost:8080/api/user_data`)
    .then((response) => {
      this.setState({userId: response.data.id})
    })
  }

  getTasks() {
    axios.get(`http://localhost:8080/api/tasks/` + this.state.userId)
    .then((response) => {
        // console.log(response)
        this.setState({getTasks: true})
        this.setState({tasks: response.data})
      })
  }

  taskHandler = (event) => {
    let id = event.target.id
    let task = event.target.value
    const val = event.target.value;
    clearTimeout(this.typingTimer);
    this.typingTimer = setTimeout(() => {
    if (val) {
      this.putTask(id, task, false)
    }
    }, 500);
  }

  completeHandler = (event) => {
    let id = event.target.id
    let checked = event.target.value
    let task
    this.state.tasks.map((task, index) => 
      task.id === id ?
      task = task.task : null
    );
    if (checked === "on") {
      this.putTask(id, task, true)
    }
    else {
      this.putTask(id, task, false)
    }
  }

  newTaskHandler = (event) => {
    this.setState({newTask: event.target.value});
  }

  newTaskSubmit() {
    let submitTask = this.state.newTask
    console.log("HERE1", submitTask)
    if (submitTask) {
      console.log("HERE2")
      axios.post(`http://localhost:8080/api/task`, {
        task: submitTask,
        complete: false,
        user_id: this.state.userId
        })
        .then(res => {
          console.log(res)
            // this.props.history.push(res.data)
        })
    }
  }

  postTask(task) {
    axios.post(`http://localhost:8080/api/task`, {
      task: task,
      complete: false,
      user_id: this.state.userId
      })
      .then(res => {
        console.log(res)
          // this.props.history.push(res.data)
      })
  }

  putTask(id, task, complete) {
    axios.put(`http://localhost:8080/api/task/` + id, {
      task: task,
      complete: complete,
      user_id: this.state.userId
      })
      .then(res => {
          // this.props.history.push(res.data)
      })
  }

  render() {
    console.log("state",this.state.newTask)
    if (!this.state.getTasks && this.state.userId) {
      this.getTasks();
    }
    let inputs
    let checkboxes
    if (this.state.tasks !== {}) {
      inputs = this.state.tasks.map((task, index) => 
          <input
          type='text'
          id= {task.id}
          placeholder = {task.task}
          onChange={this.taskHandler}
          />
        );
      checkboxes = this.state.tasks.map((task, index) =>
          task.complete === true ?
          <input 
            type="checkbox" 
            id={task.id}
            onChange={this.completeHandler}
            checked
            /> :
            <input 
            type="checkbox" 
            id={task.id}
            onChange={this.completeHandler}
            />
        );
    }

    return (
    <React.Fragment>
      <form>
        {inputs}
        {checkboxes}
      </form>

      <form onSubmit={this.newTaskSubmit}>
      <p>Add new task</p>
        <input
        type='text'
        onChange={this.newTaskHandler}
        />
        <input
        type='submit'
        />
      </form>
    </React.Fragment>
    );
  }
}

export default TasksEditor ;
