import React from 'react';
import axios from 'axios';

class TasksEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        taskList: ["Task 1", "Task 2", "Task 3", "Task 4", "Task 5", "Task 6"],
        completeList: [false, false,false,false,false,false,],
        task: '',
        complete: false,
        userId: null
    };
  }

  taskHandler = (event) => {
    const newList = this.state.taskList.slice() //copy the array
    newList[event.target.id] = event.target.value //execute the manipulations
    this.setState({taskList: newList}) //set the new state
  }

  submitHandler = (event) => {
    event.preventDefault();
    if (this.state.task !== "") {
        axios.post(`http://localhost:8080/api/tasks`, {
            task: this.state.task,
            complete: this.state.complete
        })
        .then(res => {
            // this.props.history.push(res.data)
        })
    }
  }

  render() {
    console.log(this.state.taskList)
    let inputs = this.state.taskList.map((task, index) => (
      <input
        type='text'
        id= {index}
        placeholder = {task}
        // value="Submit"
        onChange={this.taskHandler}
        />
    ));
    let checkboxes = this.state.taskList.map((checkbox, index) => (
        <input type="checkbox" id={index}/>
  ));
    return (
    <React.Fragment>
      <form onSubmit={this.submitHandler}>
        {inputs}
        {checkboxes}
        {/* <input
        type='text'
        placeholder="Task 1"
