import React from 'react';
import axios from 'axios';
import ImageUploader from 'react-images-upload';
import styles from './styles/image-page.module.css'; 
import { Grid, Row, Col } from 'react-flexbox-grid';

class ImageUpload extends React.Component {
    constructor(props) {
        super(props);
         this.state = { 
            pictures: [],
            userId: null
            };
         this.onDrop = this.onDrop.bind(this);
    }

    componentDidMount() {
        if (!this.state.userId) {
          this.getUserId();
        }
      }

    getUserId() {
        axios.get(`http://localhost:8080/api/user_data`)
        .then((response) => {
            this.setState({userId: response.data.id})
        })
    }

    onDrop(picture) {
        this.setState({
            pictures: this.state.pictures.concat(picture),
        });
    }

    uploadImage() {
        const formData = new FormData()
        formData.append('upload', this.state.pictures[0])
        console.log("formData",formData)
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }
        axios.post("http://localhost:8080/api/upload", formData,config, {
        }).then(res => {
            console.log(res)
        })
    }

  render() {
      console.log(this.state.pictures[0])
      if (this.state.pictures.length > 0) {
          this.uploadImage()
      }
    return (
        <React.Fragment>
            <Row className={styles.title}>
                <Col xs={12} >
                    <h1>Image Upload</h1>
                </Col>
            </Row>
            <Row className={styles.wrapper} >
                <Col xs={6} >
                    <ImageUploader
                    withIcon={true}
                    buttonText='Choose images'
                    onChange={this.onDrop}
                    imgExtension={['.jpg', '.gif', '.png', '.gif']}
                    maxFileSize={5242880}
                />
                </Col>
            </Row>

        </React.Fragment>
    );
  }
}

export default ImageUpload;
