import React from 'react';
import axios from 'axios';

class TasksEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        taskList: ["Task 1", "Task 2", "Task 3", "Task 4", "Task 5", "Task 6", "Task 7"],
        completeList: [false, false,false,false,false,false],
        userId: null,
        getTasks: false,
        tasks: []
    };
  }

  typingTimer = null;

  componentDidMount() {
    if (!this.state.userId) {
      this.getUserId();
    }
  }

  getUserId() {
    axios.get(`http://localhost:8080/api/user_data`)
    .then((response) => {
      this.setState({userId: response.data.id})
    })
  }

  getTasks() {
    const newTaskList = this.state.taskList.slice()
    const newCompList = this.state.completeList.slice()
    axios.get(`http://localhost:8080/api/tasks/` + this.state.userId)
    .then((response) => {
        response.data.map((task,i) => 
          newTaskList[i] = task.task
        );
        response.data.map((task,i) => 
          newCompList[i] = task.complete,
      );
      console.log(response)
        this.setState({taskList: newTaskList})
        this.setState({completeList: newCompList})
        this.setState({getTasks: true})
        this.setState({tasks: response.data})
      })
  }

  taskHandler = (event) => {
    this.setState(prevState => {
      let tasks = Object.assign({}, prevState.tasks);  // creating copy of state variable jasper
      tasks.task = event.target.value;                     // update the name property, assign a new value                 
      return { tasks };                                 // return new object jasper object
    })


    const newList = this.state.taskList.slice()
    newList[event.target.id] = event.target.value
    this.setState({taskList: newList})
    // const val = event.target.value;
    //    clearTimeout(this.typingTimer);
    //    this.typingTimer = setTimeout(() => {
    //     if (val) {
    //       // console.log("fupps",event.target.value)
    //       // this.submitUpdates()
    //       // window.alert('Stopped typing !');
    //     }
    //    }, 500);
  }

  completeHandler = (event) => {
    const newList = this.state.completeList.slice()
    if (event.target.value === "on") {
      newList[event.target.id] = true
    }
    else {
      newList[event.target.id] = false
    }
    this.setState({completeList: newList})
  }

  submitUpdates() {
    // this.state.taskList.map((task) => 
    //     // !task.startsWith("Task ") ? this.postTask(task) : null
    //     this.postTask(task)
    //   );
  }

  postTask(task) {
    axios.post(`http://localhost:8080/api/task`, {
      task: task,
      complete: false,
      user_id: this.state.userId
      })
      .then(res => {
          // this.props.history.push(res.data)
      })
  }

  render() {
    if (!this.state.getTasks && this.state.userId) {
      this.getTasks();
    }
    let inputs
    let checkboxes
    // console.log("tasks",this.state.tasks)
    if (this.state.tasks !== {}) {
      inputs = this.state.tasks.map((task, index) => 
          <input
          type='text'
          id= {task.id}
          value = {task.task}
          onChange={this.taskHandler}
          />
        );
        checkboxes = this.state.tasks.map((task, index) =>
              task.complete == true ?
              <input 
                type="checkbox" 
                id={task.id}
                onChange={this.completeHandler}
                checked
                /> :
                <input 
                type="checkbox" 
                id={index}
                onChange={this.completeHandler}
                />
        );
    }
    // inputs = this.state.taskList.map((task, index) => (
    //   <input
    //     type='text'
    //     id= {index}
    //     value = {task.task}
    //     onChange={this.taskHandler}
    //     />
    // ));
    // checkboxes = this.state.completeList.map((checkbox, index) => (
    //     checkbox.complete == true ?
    //     <input 
    //       type="checkbox" 
    //       id={index}
    //       onChange={this.completeHandler}
    //       checked
    //       /> :
    //       <input 
    //       type="checkbox" 
    //       id={index}
    //       onChange={this.completeHandler}
    //       />
  // ));
    return (
    <React.Fragment>
      <form>
        {inputs}
        {checkboxes}
      </form>
    </React.Fragment>
    );
  }
}

export default TasksEditor ;
