import React from 'react';
import axios from 'axios';

class ImagePreview extends React.Component {
    constructor(props) {
        super(props);
        this.state = { 
            userId: null,
            getImages: false,
            images: [],
        };
      }

      typingTimer = null;

      componentDidMount() {
        if (!this.state.userId) {
          this.getUserId();
        }
      }

      getUserId() {
        axios.get(`http://localhost:8080/api/user_data`)
        .then((response) => {
          this.setState({userId: response.data.id})
        })
      }

      getImages() {
        axios.get(`http://localhost:8080/api/images/` + this.state.userId)
        .then((response) => {
            this.setState({getImages: true})
            this.setState({images: response.data})
          })
      }

      render() {
        if (this.state.userId && !this.state.getImages) {
            this.getImages()
        }
      let tasks
      if (this.state.tasks !== {}) {
        tasks = this.state.tasks.map((task, index) => 
            index < 3 ?
            <p>{task.task}</p>
            : null
          );
      }
    return (
        <React.Fragment>
            {tasks}
        </React.Fragment>
    );
  }
}

export default ImagePreview;
