import React from 'react';
import axios from 'axios';

class WeatherPreview extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        lat: 51.4939446,
        lon: -0.0841836
    };
  }

  componentDidMount() {
    let currentComponent = this;
    if ("geolocation" in navigator) {
        // navigator.geolocation.getCurrentPosition(function(position) {
        //     console.log("yurt",position.coords.latitude, position.coords.longitude)
        //     if (position.coords.latitude && position.coords.longitude) {
        //         this.setState({lat: position.coords.latitude});
        //         this.setState({lon: position.coords.longitude});
        //     }
        //   });

        navigator.geolocation.getCurrentPosition(function(position) {

            if (position.coords.latitude && position.coords.longitude) {
                this.setState({lat: position.coords.latitude});
                this.setState({lon: position.coords.longitude});
                console.log("Lat is :", typeof position.coords.latitude);
            console.log("Lon is :", typeof position.coords.longitude);
            }
          });
    } 
  }

  render() {
    // console.log("Latitude is :", this.state.lat);
    // console.log("Longitude is :", this.state.lon);
    return (
      <h1>{this.state.name}</h1>
    );
  }
}

export default WeatherPreview;
