import React from 'react';
import axios from 'axios';

class TasksEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        taskList: ["Task 1", "Task 2", "Task 3", "Task 4", "Task 5", "Task 6"],
        completeList: [false, false,false,false,false,false,],
        userId: null,
        getTasks: false
    };
  }

  typingTimer = null;

  componentDidMount() {
    if (!this.state.userId) {
      this.getUserId();
    }
  }

  getUserId() {
    axios.get(`http://localhost:8080/api/user_data`)
    .then((response) => {
      this.setState({userId: response.data.id})
    })
  }

  getTasks() {
    const newTaskList = this.state.taskList.slice()
    const newCompList = this.state.completeList.slice()
    axios.get(`http://localhost:8080/api/tasks/` + this.state.userId)
    .then((response) => {
        response.data.map((task,i) => 
          newTaskList[i] = task.task
        );
        response.data.map((task,i) => 
          newCompList[i] = task.complete,
      );
      console.log(response)
        this.setState({taskList: newTaskList})
        this.setState({completeList: newCompList})
        this.setState({getTasks: true})
      })
  }

  taskHandler = (event) => {
    const newList = this.state.taskList.slice()
    newList[event.target.id] = event.target.value
    this.setState({taskList: newList})
    // const val = event.target.value;
    //    clearTimeout(this.typingTimer);
    //    this.typingTimer = setTimeout(() => {
    //     if (val) {
    //       window.alert('Stopped typing !');
    //     }
    //    }, 500);
  }

  completeHandler = (event) => {
    const newList = this.state.completeList.slice()
    if (event.target.value === "on") {
      newList[event.target.id] = true
    }
    else {
      newList[event.target.id] = false
    }
    this.setState({completeList: newList})
  }

  submitHandler = (event) => {
    event.preventDefault();
    if (this.state.task !== "") {
        axios.post(`http://localhost:8080/api/tasks`, {
            task: this.state.task,
            complete: this.state.complete
        })
        .then(res => {
            // this.props.history.push(res.data)
        })
    }
  }

  render() {
    if (!this.state.getTasks && this.state.userId) {
      this.getTasks();
    }
    console.log(this.state)

    let inputs = this.state.taskList.map((task, index) => (
      <input
        type='text'
        id= {index}
        value = {task}
        onChange={this.taskHandler}
        />
    ));
    let checkboxes = this.state.taskList.map((checkbox, index) => (
        <input 
          type="checkbox" 
          id={index}
          onChange={this.completeHandler}
          />
  ));
    return (
    <React.Fragment>
      <form onSubmit={this.submitHandler}>
        {inputs}
        {checkboxes}
      </form>
    </React.Fragment>
    );
  }
}

export default TasksEditor ;
