import React from 'react';
import axios from 'axios';

class TasksEditor extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
        taskList: ["Task 1", "Task 2", "Task 3"],
        task: '',
        complete: false,
        userId: null
    };
  }

  taskHandler = (event) => {
    this.setState({task: event.target.value});
  }

  passwordHandler = (event) => {
    this.setState({task: event.target.value});
  }

  submitHandler = (event) => {
    event.preventDefault();
    if (this.state.task !== "") {
        axios.post(`http://localhost:8080/api/tasks`, {
            task: this.state.task,
            complete: this.state.complete
        })
        .then(res => {
            // this.props.history.push(res.data)
        })
    }
  }

  render() {
    let inputs = this.state.taskList.map((task, index) => (
